import { defineStore } from "pinia"
import { Ref, ref } from "vue"

export type Album = {
  id: string|undefined,
  name: string|undefined,
  artist: string|undefined
  releaseYear?: string|undefined
  image?: string|undefined
}

export const useAlbumStore = defineStore('records', () => {
  const albums: Ref<Array<Album>> = ref([
    {
      id: 'abbey-road',
      name: 'Abbey Road',
      artist: 'The Beatles',
      releaseYear: '1964',
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 'entroducing',
      name: 'Entroducing',
      artist: 'DJ Shadow',
      releaseYear: '1996',
      image: "data:image/jpeg;base64,",
    },
    {
      id: 'bewilderment',
      name: 'Bewilderment',
      artist: 'Pale Jay',
      releaseYear: '2023',
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 'doggy-style',
      name:'DoggyStyle',
      artist: 'Snoop Dogg',
      releaseYear: '1993',
      image: 'data:image/jpeg;base64,',
    },
  ])

  const getAllAlbums = () => {
    return albums
  }

  const getAlbumById = (id: string) => {
    return albums.value.find(r => r.id === id)
  }

  const addAlbum = (album: Partial<Album>) => {
    const newAlbum = {
      id: album?.name?.toLowerCase().replace(/\s+/g, '-'),
      name: album.name,
      artist: album.artist,
      image: album.image,
      releaseYear: album.releaseYear,
    }

    albums.value.unshift(newAlbum);
  }

  return {
    albums,
    getAllAlbums,
    getAlbumById,
    addAlbum,
  }

})
